import React, { useEffect, useState } from 'react';
import { Text, StyleSheet, View } from 'react-native';
import Button from './Button';
import { winner, hasMovesLeft, find_best_move } from '../logic/index';

let initValues = ['', '', '', '', '', '', '', '', ''];

const Game = () => {
  const [player, setPlayer] = useState(0);
  const [values, setValues] = useState(initValues);
  const [finished, setFinished] = useState(false);
  const [win, setWin] = useState('');

  useEffect(() => {
    const win = winner(values);

    if (win) {
      setFinished(true);
    }

    if (player === 1 && !finished) {
      const move = find_best_move(values);
      setValues(values.map((el, i) => (i === move ? 'X' : el)));
      setPlayer(0);
    }
  }, values);

  const handleTouch = (index) => {
    if (player === 0 && values[index] === '') {
      setValues(values.map((el, i) => (i === index ? 'O' : el)));
      setPlayer(1);
    }
  };

  const renderButtons = () => {
    return values.map((value, index) => {
      return (
        <Button
          key={index}
          index={index}
          value={value}
          handleTouch={(index) => handleTouch(index)}
        />
      );
    });
  };

  return (
    <View style={styles.gameBoard}>
      <Text style={styles.text}></Text>
      {renderButtons()}
    </View>
  );
};

const styles = StyleSheet.create({
  text: { fontSize: 30 },
  gameBoard: {
    padding: '5%',
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
});

export default Game;
