import React, { useEffect, useState } from 'react';
import { Text, StyleSheet, View, Button as RButton } from 'react-native';
import Button from './Button';
import { winner, hasMovesLeft, find_best_move } from '../logic/index';

let initValues = ['', '', '', '', '', '', '', '', ''];

const Game = () => {
  const [player, setPlayer] = useState(0);
  const [values, setValues] = useState(initValues);
  const [finished, setFinished] = useState(false);
  const [win, setWin] = useState('');

  useEffect(() => {
    const w = winner(values);

    if (w) {
      setFinished(true);
      setWin(w);
      return;
    }

    if (player === 1 && !finished) {
      const move = find_best_move(values);
      setValues(values.map((el, i) => (i === move ? 'X' : el)));
      setPlayer(0);
    }
  }, values);

  const handleTouch = (index) => {
    if (player === 0 && values[index] === '') {
      setValues(values.map((el, i) => (i === index ? 'O' : el)));
      setPlayer(1);
    }
  };

  const renderButtons = () => {
    return values.map((value, index) => {
      return (
        <Button
          key={index}
          index={index}
          value={value}
          handleTouch={(index) => handleTouch(index)}
        />
      );
    });
  };

  return (
    <View style={styles.container}>
      <Text style={styles.text}>Your move</Text>
      <View style={styles.gameBoard}>{renderButtons()}</View>
      <Text style={styles.text}>{win === 'O' ? 'Player wins' : 'PC wins'}</Text>
      <View style={styles.resetButton}>
        <RButton style={styles.resetButton} title='Reset'></RButton>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: { alignItems: 'center' },
  text: { fontSize: 30 },
  gameBoard: {
    padding: '5%',
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  resetButton: { width: '80%', height: '30%', marginTop: 5 },
});

export default Game;
